mapper.insert:

	SELECT COALESCE(MAX(sample_no), 0) + 1 AS sampleNo
	FROM t_sample


INSERT INTO t_sample (
sample_no
,title
,description
) VALUES (
#{sampleNo}
,#{title}
,#{description}
)

mapper.update:
UPDATE t_sample
SET title = #{title}
,description = #{description}
WHERE sample_no = #{sampleNo}
